import './App.css';
import React, {useState, useEffect} from 'react';
import LoadingSpinner  from '../src/UIElements/LoadingSpinner'
import ErrorModal from './UIElements/ErrorModal';


function App() {

const [fetchedAuthor, setFetchAuthor] = useState(null);
const [fetchedTitle, setFetchedTitle] = useState(null);
const [fetchedPageNumber, setFetchedPageNumber] = useState(null);
const [fetchedReadStatus, setFetchedReadStatus] = useState(null);
const[fetchData, setFetchedData] = useState([]);
const [searchedTitle, setSearchedTitle] = useState(null)

const [loading, setLoading] = useState(false)
const [error, setError] = useState(false)

useEffect(() => {

}, [])

//need swork on proper function execution 

  const fetchingFunction = () => {
    setLoading(true)
    try{
      fetch('http://localhost:5000/books', {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          'Accept': 'application/json'
        }
      }).then(function(response) {

         return response.json();

      }).then(data =>  setFetchedData(data))
      .then(() => {

        for(let i = 0; i < fetchData.length; i++){
          if(searchedTitle === fetchData[i].title){
              setFetchAuthor(fetchData[i].author)
              setFetchedPageNumber(fetchData[i].pages)
              setFetchedTitle(fetchData[i].title)
              setFetchedReadStatus(fetchData[i].readStatus)
              console.log(fetch)
         } else {


         }


       }


     }
    )
    } catch(err) {
      console.log(err)


    }

return setLoading(false)
}



//  data.filter(book => 
//   book.includes(searchedTitle, 
//     setFetchAuthor(book.author),
//     setFetchedPageNumber(book.pages),
//     setFetchedTitle(book.title),
//     setFetchedReadStatus(book.readStatus)

//     ))



 const addAuthorHandler = () => {}


  return (
<>
{error && <ErrorModal setError={setError} />}
{loading && <LoadingSpinner asOverlay />}
<section>

    <div className="container-fluid">
      <div className="row text-white text-center">
        <div className="col m-4">
          <h1 className="display-3 mb-3 mt-3">Welcome to Our Library</h1>

          <div className="row  d-flex justify-content-center">
              <div className="underline mb-5 "></div> 
          </div>
        </div>     
      </div>
    </div>
 </section>



 <section>
   <div className="container-fluid">
     <div className="row align-items-center">
       <div className="col-lg-4 my-4">
         <div className="card mx-auto library-card text-center p-4">
            <div className="card-body">
              <p className="small card-text">Copyrighted Material</p>
              <h3 className="card-title text-uppercase display-6">Library Card</h3>

               <div className="row  d-flex justify-content-center">
                  <div className="card-underline mb-5"></div> 
                </div>

              <div className="underline-space mb-3"></div>
              <ul className="list-group list-group-flush">
                <li className="list-group-item mb-3" id="Author">Author
                <p>{fetchedAuthor}</p>
                </li>
                <li className="list-group-item mb-3" id="Title">Title
                <p>{fetchedTitle}</p>
                </li>
                <li className="list-group-item mb-3">Number of Pages
                <p>{fetchedPageNumber}</p>
                </li>
                <li className="list-group-item mb-3 readStatus">Read Status
                <p>{fetchedReadStatus}</p></li>
              </ul> 
            </div>
         </div>
       </div>


         <div className="col-lg-4">
            <form className="text-center bg-light p-5 rounded my-4 mx-auto addAuthor" id="findAuthor" >
              <div className="form-group mx-auto">
                <h3 className="card-title text-uppercase display-6">Find a Book</h3>

                <div className="row  d-flex justify-content-center">
                  <div className="card-underline mb-5 "></div> 
                </div>

                <li className="list-group-item mb-3" id="find">Make sure to capatilize your book title and
                check for spelling before it appears on your library card ^_^</li>

                <input
                 className="form-control mb-4 bookTitle" 
                 type="text" 
                 placeholder="Title" 
                 name="TITLE" 
                 value={searchedTitle} 
                 onInput={ e => setSearchedTitle(e.target.value)}
                 id="bookInput"
                //set value of books 
                  />
                <br />
              </div>
              <button 
              type="button" 
              className="btn btn-primary btn-lg rounded-pill px-5" 
              id="findButton"
              onClick={fetchingFunction}>Submit</button>
            </form>
         </div>


        <div className="col-lg-4">
            <form className="text-center bg-light p-5 rounded my-4 mx-auto" onSubmit={addAuthorHandler}>
              <div className="form-group mx-auto">
                <h3 className="card-title text-uppercase display-6">Add an author</h3>

                <div className="row  d-flex justify-content-center">
                  <div className="card-underline mb-5 "></div> 
                </div>

                <input className="form-control mb-4 author" type="text" placeholder="Author" name="AUTHOR" value="" />
                <input className="form-control mb-4 title" type="text" placeholder="Title" name="TITLE" value="" />
                <input className="form-control mb-4 pages" type="text" placeholder="Number of Pages?" name="PAGES" value="" />
                <input className="form-control mb-4 readstatus" type="text" placeholder="Read Status?" name="READSTATUS" value="" />
                <br />
              </div>
              <button type="button" className="btn btn-primary btn-lg rounded-pill px-5" id="addButton">Submit</button>
            </form>
         </div>  
     </div>
   </div>
 </section>

</>
  );
}

export default App;
